import React from 'react';
import styled from 'styled-components';

const StyledWrapper = styled.div`
  img {
    display: block;
    max-width: 60%;
    border-radius: 1rem;
  }
  .stack {
    width: 55%;
    max-width: 400px;
    transition: 0.25s ease;
    margin: 0 auto;
    &:hover {
      transform: rotate(5deg);
      .card:before {
        transform: translatey(-2%) rotate(-4deg);
      }
      .card:after {
        transform: translatey(2%) rotate(4deg);
      }
    }
  }
  .card {
    aspect-ratio: 3 / 2;
    border: 4px solid #AABF91;
    background-color: #fff;
    position: relative;
    transition: 0.15s ease;
    cursor: pointer;
    padding: 5% 5% 15% 5%;
    border-radius: 1rem;
    overflow: hidden;
    &:before,
    &:after {
      content: "";
      display: block;
      position: absolute;
      height: 100%;
      width: 100%;
      border: 4px solid #AABF91;
      background-color: #fff;
      transform-origin: center center;
      z-index: -1;
      transition: 0.15s ease;
      top: 0;
      left: 0;
      border-radius: 1rem;
    }
    &:before {
      transform: translatey(-2%) rotate(-6deg);
    }
    &:after {
      transform: translatey(2%) rotate(6deg);
    }
  }
  .image {
    width: 100%;
    border: 4px solid #AABF91;
    background-color: #eee;
    aspect-ratio: 1 / 1;
    position: relative;
    object-fit: cover;
    border-radius: 1rem;
  }
  .browser-warning {
    margin-bottom: 4rem;
  }
  @supports (aspect-ratio: 1 / 1) {
    .browser-warning {
      display: none;
    }
  }
`;

const Card = ({ imgSrc, alt }) => {
  return (
    <StyledWrapper>
      <div>
        <div className="stack">
          <div className="card">
            <img src={imgSrc} alt={alt || 'Profile'} className="image" />
          </div>
        </div>
      </div>
    </StyledWrapper>
  );
};

export default Card;
