import React from 'react';

export default function BackToTopButton() {
  const scrollToTop = () => {
    window.scrollTo({
      top: 0,
      behavior: 'smooth',
    });
  };

  return (
    <button
      onClick={scrollToTop}
      className="group w-11 h-11 rounded-full bg-gradient-to-b from-[#AABF91] to-[#31572c] flex items-center justify-center relative border-none cursor-pointer"
    >
      <svg
        height="1.2em"
        className="arrow text-white group-hover:animate-slide-in-bottom"
        viewBox="0 0 512 512"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          d="M233.4 105.4c12.5-12.5 32.8-12.5 45.3 0l192 192c12.5 12.5 12.5 32.8 0 45.3s-32.8 12.5-45.3 0L256 173.3 86.6 342.6c-12.5 12.5-32.8 12.5-45.3 0s-12.5-32.8 0-45.3l192-192z"
          fill="white"
        />
      </svg>
      <p className="absolute bottom-[-18px] text-white text-[0.7em] w-[100px] flex items-center justify-center opacity-0 group-hover:opacity-100 transition-opacity duration-700">
        Back to Top
      </p>
    </button>
  );
}
